SELECT AR.Name, A.Title, T.Name, G.Name, M.Name, T.UnitPrice FROM Track AS T LEFT OUTER JOIN InvoiceLine AS I ON T.TrackId=I.TrackId JOIN Album AS A ON T.AlbumId=A.AlbumId join Artist AS AR ON AR.ArtistId=A.ArtistId join Genre AS G ON G.GenreId=T.GenreId join MediaType AS M ON T.MediaTypeId=M.MediaTypeId WHERE I.TrackId IS NULL AND Title IN('Vinicius De Moraes','Lost, Season 1') group by AR.Name, A.Title, T.Name, G.Name, M.Name, T.UnitPrice order by AR.Name asc;